import React, { useState, useEffect } from 'react';
import { getPackages, createPackage, deletePackage } from '../api';

const PackagesPage = () => {
  const [packages, setPackages] = useState([]);
  const [name, setName] = useState('');
  const [colors, setColors] = useState([]);
  const [colorNum, setColorNum] = useState(10);

  useEffect(() => {
    const fetchPackages = async () => {
      const data = await getPackages();
      setPackages(data);
    };
    fetchPackages();
  }, []);

  const handleCreatePackage = async () => {
    const newPackage = {
      name,
      colors,
      color_num: colorNum
    };
    await createPackage(newPackage);
    setName('');
    setColors([]);
    setColorNum(10);
    const data = await getPackages();
    setPackages(data);
  };

  const handleDeletePackage = async (id) => {
    await deletePackage(id);
    const data = await getPackages();
    setPackages(data);
  };

  return (
    <div style={{ marginLeft: '220px', padding: '20px' }}>
      <h2>套餐管理</h2>
      <input
        type="text"
        placeholder="套餐名称"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <input
        type="text"
        placeholder="选择颜色ID（用逗号分隔）"
        value={colors.join(',')}
        onChange={(e) => setColors(e.target.value.split(','))}
      />
      <input
        type="number"
        placeholder="颜色克数"
        value={colorNum}
        onChange={(e) => setColorNum(e.target.value)}
      />
      <button onClick={handleCreatePackage}>创建套餐</button>
      <table>
        <thead>
          <tr>
            <th>套餐名称</th>
            <th>颜色ID</th>
            <th>颜色克数</th>
            <th>操作</th>
          </tr>
        </thead>
        <tbody>
          {packages.map(pkg => (
            <tr key={pkg.id}>
              <td>{pkg.name}</td>
              <td>{pkg.colors}</td>
              <td>{pkg.color_num}</td>
              <td>
                <button onClick={() => handleDeletePackage(pkg.id)}>删除</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PackagesPage;
