import React, { useState, useEffect } from 'react';
import { getPackages, createPackage, deletePackage, createOrder, getColors } from '../api';

const OrdersPage = () => {
  const [packages, setPackages] = useState([]);
  const [selectedPackage, setSelectedPackage] = useState(null);
  const [packageNum, setPackageNum] = useState(1);

  useEffect(() => {
    const fetchPackages = async () => {
      const data = await getPackages();
      setPackages(data);
    };
    fetchPackages();
  }, []);

  const handleCreateOrder = async () => {
    if (!selectedPackage) return;

    const orderData = {
      package_name: selectedPackage.name,
      package_num: packageNum,
    };

    try {
      await createOrder(orderData);  // 创建订单
      setPackageNum(1);
    } catch (error) {
      console.error("Error creating order:", error);
    }
  };

  return (
    <div style={{ marginLeft: '220px', padding: '20px' }}>
      <h2>订单管理</h2>

      <div>
        <label>选择套餐:</label>
        <select
          onChange={(e) => {
            const pkg = packages.find(p => p.id === parseInt(e.target.value));
            setSelectedPackage(pkg);
          }}
        >
          <option value="">-- 选择套餐 --</option>
          {packages.map(pkg => (
            <option key={pkg.id} value={pkg.id}>{pkg.name}</option>
          ))}
        </select>
      </div>

      {selectedPackage && (
        <div>
          <h4>套餐详情:</h4>
          <p><strong>套餐名称:</strong> {selectedPackage.name}</p>
          <p><strong>选择颜色:</strong> {selectedPackage.colors}</p> {/* 显示套餐的颜色名称 */}
          <p><strong>颜色克数:</strong> {selectedPackage.color_num}</p>
          <input
            type="number"
            placeholder="订单数量"
            value={packageNum}
            onChange={(e) => setPackageNum(e.target.value)}
          />
          <button onClick={handleCreateOrder}>创建订单</button>
        </div>
      )}
    </div>
  );
};

export default OrdersPage;
