import React, { useState, useEffect } from 'react';
import { getPackages, createPackage, deletePackage, getColors } from '../api';

const PackagesPage = () => {
  const [packages, setPackages] = useState([]);
  const [name, setName] = useState('');
  const [colors, setColors] = useState([]);  // 存储选中的颜色ID
  const [colorNum, setColorNum] = useState(10);
  const [allColors, setAllColors] = useState([]);  // 存储所有颜色

  useEffect(() => {
    const fetchPackages = async () => {
      const data = await getPackages();
      setPackages(data);
    };
    fetchPackages();

    // 获取所有颜色
    const fetchColors = async () => {
      const data = await getColors();  // 使用您提供的 getColors API
      setAllColors(data);  // 存储所有颜色信息
    };
    fetchColors();
  }, []);

  const handleCreatePackage = async () => {
    const newPackage = {
      name,
      colors,  // 存储选中的颜色ID
      color_num: colorNum
    };
    await createPackage(newPackage);  // 使用您提供的 createPackage API
    setName('');
    setColors([]);
    setColorNum(10);
    const data = await getPackages();
    setPackages(data);
  };

  const handleDeletePackage = async (id) => {
    await deletePackage(id);  // 使用您提供的 deletePackage API
    const data = await getPackages();
    setPackages(data);
  };

  const handleColorChange = (e) => {
    const selectedColorId = e.target.value;
    // 如果勾选框被选中，就将选中的颜色ID添加到 colors 数组；否则移除它
    if (e.target.checked) {
      setColors(prevColors => [...prevColors, selectedColorId]);
    } else {
      setColors(prevColors => prevColors.filter(id => id !== selectedColorId));
    }
  };

  // 获取颜色名称，用来显示颜色框
  const getColorById = (id) => {
    const color = allColors.find(c => c.id === parseInt(id));
    return color ? color.name : '';
  };

  return (
    <div style={{ marginLeft: '220px', padding: '20px' }}>
      <h2>套餐管理</h2>
      <input
        type="text"
        placeholder="套餐名称"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />

      <div>
        <label>选择颜色:</label>
        <div>
          {allColors.map(color => (
            <div key={color.id}>
              <input
                type="checkbox"
                value={color.id}
                checked={colors.includes(color.id.toString())}  // 判断该颜色是否被选中
                onChange={handleColorChange}
              />
              <label>{color.name}</label>
            </div>
          ))}
        </div>
      </div>

      <div style={{ marginTop: '10px' }}>
        <h4>已选择颜色:</h4>
        <ul>
          {colors.map(colorId => (
            <li key={colorId}>{getColorById(colorId)}</li>  // 显示选择的颜色名称
          ))}
        </ul>
      </div>

      <input
        type="number"
        placeholder="颜色克数"
        value={colorNum}
        onChange={(e) => setColorNum(e.target.value)}
      />
      <button onClick={handleCreatePackage}>创建套餐</button>

      <table>
        <thead>
          <tr>
            <th>套餐名称</th>
            <th>颜色ID</th>
            <th>颜色克数</th>
            <th>操作</th>
          </tr>
        </thead>
        <tbody>
          {packages.map(pkg => (
            <tr key={pkg.id}>
              <td>{pkg.name}</td>
              <td>{pkg.colors}</td>
              <td>{pkg.color_num}</td>
              <td>
                <button onClick={() => handleDeletePackage(pkg.id)}>删除</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PackagesPage;
