import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Packages() {
  const [packages, setPackages] = useState([]);
  const [colors, setColors] = useState([]);
  const [newPkg, setNewPkg] = useState({ name: '', colors: [], deduction: 0 });

  useEffect(() => {
    axios.get('http://localhost:5000/colors').then(res => setColors(res.data));
    axios.get('http://localhost:5000/packages').then(res => setPackages(res.data));
  }, []);

  const addPackage = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5000/packages', {
      ...newPkg,
      colors: newPkg.colors.map(c => c.id)
    });
    setNewPkg({ name: '', colors: [], deduction: 0 });
    axios.get('http://localhost:5000/packages').then(res => setPackages(res.data));
  };

  const deletePackage = async (id) => {
    await axios.delete(`http://localhost:5000/packages/${id}`);
    setPackages(packages.filter(pkg => pkg.id !== id));
  };

  return (
    <div>
      <h2>套餐管理</h2>

      <form onSubmit={addPackage} className="mb-4">
        <input
          type="text"
          className="form-control mb-2"
          placeholder="套餐名称"
          value={newPkg.name}
          onChange={e => setNewPkg({...newPkg, name: e.target.value})}
        />
        <select
          multiple
          className="form-control mb-2"
          onChange={e => setNewPkg({
            ...newPkg,
            colors: Array.from(e.target.selectedOptions, opt =>
              colors.find(c => c.id == opt.value))
          })}
        >
          {colors.map(color => (
            <option key={color.id} value={color.id}>
              {color.name} ({color.grams}g)
            </option>
          ))}
        </select>
        <input
          type="number"
          className="form-control mb-2"
          placeholder="单次销售扣减克数"
          value={newPkg.deduction}
          onChange={e => setNewPkg({...newPkg, deduction: e.target.value})}
        />
        <button type="submit" className="btn btn-success">添加套餐</button>
      </form>

      <div className="row">
        {packages.map(pkg => (
          <div key={pkg.id} className="col-md-4 mb-3">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">{pkg.name}</h5>
                <p className="card-text">
                  包含颜色: {pkg.colors.split(',').map(id =>
                    colors.find(c => c.id == id)?.name).join(', ')}
                </p>
                <p className="card-text">每次销售扣减: {pkg.deduction}g</p>
                <button
                  onClick={() => deletePackage(pkg.id)}
                  className="btn btn-danger"
                >
                  删除套餐
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
export default Packages;
