import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Colors() {
  const [colors, setColors] = useState([]);
  const [newColor, setNewColor] = useState({ name: '', grams: 0 });

  const fetchColors = async () => {
    const res = await axios.get('http://localhost:5000/colors');
    setColors(res.data);
  };

  useEffect(() => { fetchColors(); }, []);

  const addColor = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5000/colors', newColor);
    setNewColor({ name: '', grams: 0 });
    fetchColors();
  };

  return (
    <div>
      <h2>颜色库存管理</h2>
      <form onSubmit={addColor} className="mb-4">
        <input
          type="text"
          className="form-control mb-2"
          placeholder="颜色名称"
          value={newColor.name}
          onChange={e => setNewColor({...newColor, name: e.target.value})}
        />
        <input
          type="number"
          className="form-control mb-2"
          placeholder="库存克数"
          value={newColor.grams}
          onChange={e => setNewColor({...newColor, grams: e.target.value})}
        />
        <button type="submit" className="btn btn-primary">添加颜色</button>
      </form>

      <table className="table table-striped">
        <thead>
          <tr>
            <th>颜色名称</th>
            <th>库存克数</th>
          </tr>
        </thead>
        <tbody>
          {colors.map(color => (
            <tr key={color.id}>
              <td>{color.name}</td>
              <td>{color.grams}g</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
