import React, { useState, useEffect } from 'react';
import { getPackages, createPackage, deletePackage, getColors } from '../api';

const PackagesPage = () => {
  const [packages, setPackages] = useState([]);
  const [name, setName] = useState('');
  const [colors, setColors] = useState([]);
  const [colorNum, setColorNum] = useState(10);
  const [allColors, setAllColors] = useState([]);  // 存储所有颜色

  useEffect(() => {
    const fetchPackages = async () => {
      const data = await getPackages();
      setPackages(data);
    };
    fetchPackages();

    // 获取所有颜色
    const fetchColors = async () => {
      const data = await getColors();  // 使用您提供的 getColors API
      setAllColors(data);  // 存储所有颜色信息
    };
    fetchColors();
  }, []);

  const handleCreatePackage = async () => {
    const newPackage = {
      name,
      colors,
      color_num: colorNum
    };
    await createPackage(newPackage);  // 使用您提供的 createPackage API
    setName('');
    setColors([]);
    setColorNum(10);
    const data = await getPackages();
    setPackages(data);
  };

  const handleDeletePackage = async (id) => {
    await deletePackage(id);  // 使用您提供的 deletePackage API
    const data = await getPackages();
    setPackages(data);
  };

  const handleColorChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, option => option.value);
    setColors(selectedOptions);
  };

  return (
    <div style={{ marginLeft: '220px', padding: '20px' }}>
      <h2>套餐管理</h2>
      <input
        type="text"
        placeholder="套餐名称"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <div>
        <label>选择颜色:</label>
        <select
          multiple
          value={colors}
          onChange={handleColorChange}
        >
          {allColors.map(color => (
            <option key={color.id} value={color.id}>
              {color.name} ({color.color_id})
            </option>
          ))}
        </select>
      </div>
      <input
        type="number"
        placeholder="颜色克数"
        value={colorNum}
        onChange={(e) => setColorNum(e.target.value)}
      />
      <button onClick={handleCreatePackage}>创建套餐</button>
      <table>
        <thead>
          <tr>
            <th>套餐名称</th>
            <th>颜色ID</th>
            <th>颜色克数</th>
            <th>操作</th>
          </tr>
        </thead>
        <tbody>
          {packages.map(pkg => (
            <tr key={pkg.id}>
              <td>{pkg.name}</td>
              <td>{pkg.colors}</td>
              <td>{pkg.color_num}</td>
              <td>
                <button onClick={() => handleDeletePackage(pkg.id)}>删除</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PackagesPage;
