import React, { useState, useEffect } from 'react';
import { createOrder, deleteOrder } from '../api';

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [packageName, setPackageName] = useState('');
  const [packageNum, setPackageNum] = useState(1);

  useEffect(() => {
    const fetchOrders = async () => {
      // 这里可以使用后端 API 获取订单列表（如果有 API 提供的话）
    };
    fetchOrders();
  }, []);

  const handleCreateOrder = async () => {
    const newOrder = { package_name: packageName, package_num: packageNum };
    await createOrder(newOrder);
    setPackageName('');
    setPackageNum(1);
    // 重新拉取订单
    const data = await getOrders();  // 假设已有 `getOrders` API
    setOrders(data);
  };

  const handleDeleteOrder = async (id) => {
    await deleteOrder(id);
    // 重新拉取订单
    const data = await getOrders();  // 假设已有 `getOrders` API
    setOrders(data);
  };

  return (
    <div style={{ marginLeft: '220px', padding: '20px' }}>
      <h2>订单管理</h2>
      <input
        type="text"
        placeholder="套餐名称"
        value={packageName}
        onChange={(e) => setPackageName(e.target.value)}
      />
      <input
        type="number"
        placeholder="套餐数量"
        value={packageNum}
        onChange={(e) => setPackageNum(e.target.value)}
      />
      <button onClick={handleCreateOrder}>创建订单</button>
      <table>
        <thead>
          <tr>
            <th>订单ID</th>
            <th>套餐名称</th>
            <th>套餐数量</th>
            <th>操作</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.package_name}</td>
              <td>{order.package_num}</td>
              <td>
                <button onClick={() => handleDeleteOrder(order.id)}>删除</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OrdersPage;
