import React, { useState, useEffect } from 'react';
import { createOrder, deleteOrder, getPackages, getOrders } from '../api'; // 确保从 API 中导入 getOrders

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [packageName, setPackageName] = useState('');
  const [packageNum, setPackageNum] = useState(1);
  const [packages, setPackages] = useState([]);  // 存储所有套餐

  useEffect(() => {
    const fetchOrders = async () => {
      const data = await getOrders(); // 获取订单列表
      setOrders(data);  // 将获取到的订单数据设置到状态
    };
    fetchOrders();

    // 获取所有套餐
    const fetchPackages = async () => {
      const data = await getPackages(); // 假设你有一个 API 来获取套餐
      setPackages(data);  // 设置套餐列表
    };
    fetchPackages();
  }, []);

  const handleCreateOrder = async () => {
    const newOrder = { package_name: packageName, package_num: packageNum };
    await createOrder(newOrder);
    setPackageName('');
    setPackageNum(1);
    // 重新拉取订单
    const data = await getOrders();  // 获取最新订单数据
    setOrders(data);
  };

  const handleDeleteOrder = async (id) => {
    await deleteOrder(id);
    // 重新拉取订单
    const data = await getOrders();  // 获取最新订单数据
    setOrders(data);
  };

  return (
    <div style={{ marginLeft: '220px', padding: '20px' }}>
      <h2>订单管理</h2>

      {/* 下拉选择套餐 */}
      <div>
        <label>选择套餐:</label>
        <select
          value={packageName}
          onChange={(e) => setPackageName(e.target.value)}  // 设置选择的套餐
        >
          <option value="">请选择套餐</option>
          {packages.map(pkg => (
            <option key={pkg.id} value={pkg.name}>
              {pkg.name}
            </option>
          ))}
        </select>
      </div>

      <div>
        <input
          type="number"
          placeholder="套餐数量"
          value={packageNum}
          onChange={(e) => setPackageNum(e.target.value)}
        />
      </div>

      <button onClick={handleCreateOrder}>创建订单</button>

      <table>
        <thead>
          <tr>
            <th>订单ID</th>
            <th>套餐名称</th>
            <th>套餐数量</th>
            <th>操作</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.package_name}</td>
              <td>{order.package_num}</td>
              <td>
                <button onClick={() => handleDeleteOrder(order.id)}>删除</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OrdersPage;
